import { expectToEqual } from "shared";
import { createFetchSharedClient } from "shared-routes/fetch";
import { AppConfig } from "../../../../config/bootstrap/appConfig";
import { BrevoNotificationGateway } from "./BrevoNotificationGateway";
import { brevoNotificationGatewayRoutes } from "./BrevoNotificationGateway.routes";

describe("BrevoNotificationGateway manual", () => {
  let notificationGateway: BrevoNotificationGateway;

  beforeEach(() => {
    const config = AppConfig.createFromEnv();
    notificationGateway = new BrevoNotificationGateway(
      {
        httpClient: createFetchSharedClient(
          brevoNotificationGatewayRoutes,
          fetch,
          { onResponseSideEffect: console.log },
        ),
        emailAllowListPredicate: () => true,
        defaultSender: { email: "bob@fake.mail", name: "Immersion Facilitée" },
        blackListedEmailDomains: [],
      },
      config.apiKeyBrevo,
    );
  });

  it("should send email correctly", async () => {
    const result = await notificationGateway.sendEmail({
      kind: "TEST_EMAIL",
      recipients: ["____TO____"], // Modify for testing
      sender: {
        email: "____FROM_EMAIL____", // Modify for testing
        name: "_____FROM_NAME____", // Modify for testing
      },
      params: {
        input1: "paramètre métier 1",
        input2: "paramètre métier 2",
        url: "https://immersion-facile.beta.gouv.fr/",
      },
    });

    expectToEqual(result, {
      isOk: true,
      messageIds: [expect.any(String)],
    });

    // Please check emails has been received at recette@immersion-facile.beta.gouv.fr
    expect("reached").toBe("reached");
  });

  it("should send SMS correctly", async () => {
    const result = await notificationGateway.sendSms({
      recipientPhone: "VALID_INTERNATIONAL_FRENCH_MOBILE_PHONE_NUMBER", // Like 33611223344
      kind: "LastReminderForSignatories",
      params: {
        shortLink:
          "https://immersion-facile.beta.gouv.fr/api/to/gygr669PTEQBiTwfNycBl9nq8Pua3h5D9pv2",
      },
    });

    expectToEqual(result, {
      isOk: true,
      messageIds: [expect.anything()],
    });

    // Please check SMS has been received at VALID_INTERNATIONAL_FRENCH_MOBILE_PHONE_NUMBER
    expect("reached").toBe("reached");
  });

  const times = 50;

  it(
    `should send ${times} SMS with rate correctly`,
    async () => {
      const phones = [];

      for (let i = 0; i < times; i++)
        phones.push("VALID_INTERNATIONAL_FRENCH_MOBILE_PHONE_NUMBER"); // Like +33611223344
      await Promise.all(
        phones.map((phone, index) =>
          notificationGateway.sendSms({
            recipientPhone: phone,
            kind: "LastReminderForSignatories",
            params: {
              shortLink: `https://test-sms-${index + 1}`,
            },
          }),
        ),
      );

      // Please check SMS has been received at VALID_INTERNATIONAL_FRENCH_MOBILE_PHONE_NUMBER
      expect("reached").toBe("reached");
    },
    1100 * times,
  );

  it("should retrieve attachment content correctly", async () => {
    const downloadToken =
      "eyJmb2xkZXIiOiIyMDIzMDcxMzEyMTcxNS45Mi40Mjg0MDQyMiIsImZpbGVuYW1lIjoiaWYtbG9nby1wZS1pby5wbmcifQ";
    const base64 =
      await notificationGateway.getAttachmentContent(downloadToken);
    expect(base64).toBe(ifLogoInBase64);
  });

  it("should not retrieve attachment if it is not a blob", async () => {
    const downloadToken =
      "eyJmb2xkZXIiOiIyMDI1MDEwOTA5MDQ1OC45Ni45MTM3NjE2OTgiLCJmaWxlbmFtZSI6Ii4rQ0lERitCRUxGT1JUKy52Y2YifQ";

    const attachment =
      await notificationGateway.getAttachmentContent(downloadToken);
    expect(attachment).toBe(null);
  });

  it("should send email with attachment correctly", async () => {
    const response = await notificationGateway.sendEmail({
      kind: "TEST_EMAIL",
      recipients: ["recette@immersion-facile.beta.gouv.fr"],
      params: {
        input1: "test input 1",
        input2: "test input 2",
        url: "https://www.google.fr",
      },
      attachments: [
        {
          name: "if-logo-pe-io.png",
          content: ifLogoInBase64,
        },
      ],
    });
    // should check on mail client for
    expect(response).toBeUndefined();
  });
});

const ifLogoInBase64 =
  "";
